$("#btn-back-to-top").click(function(){$("html, body").animate({scrollTop:0},700);return!1});$(window).scroll(function(){200>$(this).scrollTop()?$("#btn-back-to-top").fadeOut():$("#btn-back-to-top").fadeIn()});$(".scroll-animate").click(function(){$("body,html").animate({scrollTop:$($(this).attr("href")).offset().top-70},800)});
